class Point {
    constructor(_x, _y, _phrase_key) {
        this.x = _x,
        this.y = _y,
        this.diameter = 5,
        this.color = 255,
        this.phrase_key = _phrase_key;
    }

    display(wandererX, wandererY) {
        push();
        // noFill();
        if (dist(wandererX, wandererY, this.x, this.y) < 30) {
            this.color = 50;
            stroke(50);
            line(wandererX, wandererY, this.x, this.y);
            console.log(this.phrase_key);
            if (phrases[this.phrase_key].isPlaying() === false && audioOn) {
                // play
                phrases[this.phrase_key].play();
            }   
        } else {
            this.color = 255;
            // this.phrase.stop();
        }
        fill(this.color);
        noStroke();
        // stroke(50, 100); // debugging
        ellipse(this.x, this.y, this.diameter, this.diameter);
        pop();
    }
}

function createAllPoints() {
    for (let i = 0; i < numPoints; i++) {
        point = new Point(
            (noise(i, 0) * canvasWidth),
            (noise(i, 1000) * canvasHeight),
            Math.floor(map(i, 0, 100, 0, 20))
        );
        Points.push(point);
    }
}

function displayAllPoints(_wandererX, _wandererY) {
    for (let i = 0; i < Points.length; i++) {
        Points[i].display(_wandererX, _wandererY);
    }
}

function toggleAudio() {
    if (audioOn) {
        audioOn = false;
        for (i = 0; i < 21; i++) {
            phrases[Points[i].phrase_key].stop();
        }
    } else {
        audioOn = true;
    }
}

