// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/p5.js

/* Observations of example
 * - Canvas
 *      - square canvas
 *      - dark gray background
 * - Dot placement
 *      - bias towards center of canvas
 *      - allows overlap
 * - Dot size
 *      - varies incrementally - not continuous
 * - Dot color
 *      - hue varies incrementally - not continuous
 *      - full saturation
 *      - full brightness
 * - Dot size and color are related
 *      - red -> violet ~ smaller -> bigger
 *      - all dots of x color are y size, and vice versa
 *      - frequency bias towards green and yellow / medium size
 */

function setup() {
    createCanvas(400, 400);
}

function draw() {
    background(50);
    noStroke();
    ellipseMode(CENTER);

    // Strategy 1: employs noise
    for (let i = 0; i < 100; i++) {
        const noiseFrequency = 50;

        // Noise placement.
        let x = (noise(i * noiseFrequency, 0) * 400);
        let y = (noise(i * noiseFrequency, 1000) * 400);

        // Noise hue assignment. Bias towards middle hues.
        // Applied at increments, not a continuously.
        colorMode(HSB, 1);
        let r = noise(i * noiseFrequency, 2000);
