    } else if (brightness > 105) {
        //make it a grass
        h = 109;
        s = map(brightness, 150, 255, 100, 0);
        // s = 100;
        b = map(brightness, 105, 150, 0, 100);
    } else if (brightness > 0) {
        //make it water
        h = 230;
        s = 100;
        b = map(brightness, 0, 100, 0, 100);
    }
    fill(h, s, b);
}

