let amplitude_slider, frequency_slider, speed_slider;
var noiseFrequency = .02;
var startX = 50;
var startY = 250;
var endX = 450;
var endY = 50;

function setup() {
    createCanvas(500, 300);

    createP('Amplitude');
    amplitude_slider = createSlider(0, 200, 120);

    createP('Frequency');
    frequency_slider = createSlider(0, .07, .2, .01);

    createP('Time Speed');
    speed_slider = createSlider(0, 3, .45);
}


function draw() {
    background(50);
    ellipseMode(CENTER);
    noiseDetail(1, .5);
    fill(255);
    noStroke();

    let w;
    for (i = 0; i < 1; i += noiseFrequency) {
        var x = lerp(startX, endX, i);
        var y = lerp(startY, endY, i);
        w = noise(x * frequency_slider.value() + millis() * speed_slider.value() * noiseFrequency / 10);
        var offsetX = w * amplitude_slider.value();
        var offsetY = noise(x) * noiseFrequency;
        ellipse(x + offsetX, y + offsetY, 10, 10);
    }
}

