function drawStream () {
  nx = 0;
  for (var i=200; i<400; i += 9) {
    ny = 0;
    for (var j=200; j<400; j += 9) {
      var angle = map (noise (nx, ny, nz), 0, 1.0, 0, 4*PI);
      var x = 50 * cos (angle);
      var y = 50 * sin (angle);
      line (i, j, i+x, j+y);
      ny += 0.003;
    }
    nx += 0.002;
  }
  nz +=0.001;
}

