function onFrame() {
    //add a new vector to the balls 
    for (var i = 0; i < amebas.length; i++) {
        var vector = new Point({
            angle: 360 * Math.random(),
            length: Math.random() * 10
        });
        amebas[i].position += vector;
    }
    createMergedAmebas();
}

function createMergedAmebas() {
    for (var i = 0; i < amebas.length; i++) {
        for (var j = 0; j < amebas.length; j++) {
            //dint comapre an ameba with itself
            if (i != j) {
                var intersections = amebas[i].getCrossings(amebas[j]);
                if (intersections.length > 0) {
                    amebas[i].strokeColor = 'null';
                    amebas[j].strokeColor = null;
                    var mergedAmebia = amebas[i].unite(amebas[j])

                    // mergedAmebia.blendMode = 'multiply';
                    amebas[i].remove();
                    // amebas[j].remove();
                    amebas[i] = mergedAmebia;
                    amebas[i].fillColor = 150;
                    amebas[i].style = {
                        strokeColor: new Color(0, 255, 0),
                        blendMode: 'multiply',
                        shadowColor: new Color(0, 255, 0),
                        // Set the shadow blur radius to 12:
                        shadowBlur: 10,
                        // Offset the shadow by { x: 5, y: 5 }
                        shadowOffset: new Point(5, 5)
                    };
                    amebas[i].opacity = .5;
                }
            }
        }
    }
}

function checkBorders() {
    var size = view.size;
    if (this.point.x < -this.radius)
        this.point.x = size.width + this.radius;
    if (this.point.x > size.width + this.radius)
        this.point.x = -this.radius;
    if (this.point.y < -this.radius)
        this.point.y = size.height + this.radius;
    if (this.point.y > size.height + this.radius)
        this.point.y = -this.radius;
}

