// USER CONFIG: Change the threshold btwn 0 and 255 for different
// line densities
const threshold = 100;


let img;
const canvasWidth = 500;
const canvasHeight = 900;
let anchors = [];

function preload() {
    img = loadImage("https://raw.githubusercontent.com/annagarbier/compform/master/face_500_select.jpg");
}

function setup() {
    createCanvas(530, 800);
}

function draw() {
    noStroke();
    translate(15, 15);
    background(250, 250, 250);
    // background(50);

    var spacing = 500 / img.width;
    for (var y = 0; y < img.height; y++) {
        for (var x = 0; x < img.width; x++) {
            var in_color = img.get(x, y);
            const r = in_color[0];
            const g = in_color[1];
            const b = in_color[2] + 10;
            let a = map(in_color[3], 0, 255, 0, 15);

            if (in_color[0] < threshold) {
                fill(r, g, b, a);
                ellipse(
                    x * spacing + spacing * .5,
                    y * spacing + spacing * .5,
                    2, 2
                );
                anchors.push([x, y]);
            }
