// in change in speed of the dots. Will need to work this out.

// Observations of prompt sketch
// Dot movement
// - For a given dot, x and y movement isn't necessarily the same
//   (i.e. offsetX != offsetY)
// - When freq is low and amp and speed are high, the whole line
//   moves together

let amplitude_slider;
let frequency_slider;
let timespeed_slider;

let startX = 50;
let startY = 75;
let endX = 450;
let endY = 75;

let timeInput = 0;

function setup() {
    createCanvas(500, 300);
    createP('Frequency');
    frequency_slider = createSlider(0, 40, 8, .01);
    createP('Amplitude');
    amplitude_slider = createSlider(0, 100, 50, .01);
    createP('Time Speed');
    timespeed_slider = createSlider(0, .002, .001, .0001);
}

function draw() {
    background(255, 5);
    ellipseMode(CENTER);
    let frequency = frequency_slider.value();
    let amplitude = amplitude_slider.value();
    let timespeed = timespeed_slider.value();
    noiseDetail(1, .5);

    fill(50, 200);
    noStroke();

    // for every increment of .02 between 0 and 1...
    for (i = 0; i < 1; i += .01) {

        let x = lerp(startX, endX, i); // move x a little closer to endX
        let y = lerp(startY, endY, i); // move y a little closer to endY

        timeInput += timespeed;

        let offsetX = noise(timeInput + (frequency * i), 0) * amplitude;
        let offsetY = noise(timeInput + (frequency * i), 1) * 4 * amplitude;

        ellipse(x + offsetX, y + offsetY, 4, 4);
    }

    translate(0, 40);
    // for every increment of .02 between 0 and 1...
    for (i = 0; i < 1; i += .01) {

        let x = lerp(startX, endX, i); // move x a little closer to endX
        let y = lerp(startY, endY, i); // move y a little closer to endY

        timeInput += timespeed;

        let offsetX = noise(timeInput + (frequency * i), 1) * amplitude;
        let offsetY = noise(timeInput + (frequency * i), 2) * 4 * amplitude;

        ellipse(x + offsetX, y + offsetY, 6, 6);
    }
    translate(0, 40);
    // for every increment of .02 between 0 and 1...
    for (i = 0; i < 1; i += .008) {

        let x = lerp(startX, endX, i); // move x a little closer to endX
        let y = lerp(startY, endY, i); // move y a little closer to endY

        timeInput += timespeed;

        let offsetX = noise(timeInput + (frequency * i), 3) * amplitude;
        let offsetY = noise(timeInput + (frequency * i), 4) * 4 * amplitude;

        ellipse(x + offsetX, y + offsetY, 5, 5);
    }
}

