function draw() {
    stroke(155);
    myTurtle.penDown();
    sectionRad = degrees_to_radians(section);
    // outerLength = (tan(sectionRad / 2) * sectionLength) * 2;
    outerLength = (sin(sectionRad / 2) * sectionLength) * 2;
    outerAngle = (180 - section) / 2;
    console.log(outerLength);
    console.log("outerAngle", outerAngle);
    myTurtle.moveForward(sectionLength);
    myTurtle.turnRight(180 - outerAngle);
    //draw Supports 
    for (let i = 0; i < divisions; i++) {

        myTurtle.moveForward(outerLength);
        myTurtle.turnRight(180 - outerAngle);
        if (i != divisions - 1) {
            myTurtle.moveForward(diagnalLength);
            myTurtle.turnRight(180 - outerAngle);
        } else {
            currentLength = diagnalLength - lengthDec;
            // console.log("currentLength", currentLength);
            myTurtle.moveForward(currentLength);
        }
    }
    //draw spiral inside
    //section length minus decrement 
    side1Length = sectionLength - lengthDec;
    spiralIn = lengthDec / divisions;
    for (let i = 0; i < 650; i++) {
        stroke(255);
