        //side 1 length  is current length going out from the center axis 
        //side 2 length is the side you will be drawing towards coming away from the middle
        side2Length = side1Length - spiralIn;
        //outter length is the new length you will draw in the spiral depending on how much you decrease teh next side 
        //inner angle in radians 
        innerRad = degrees_to_radians(section);
        innerCos = cos(innerRad);
        innerSin = sin(innerRad);
