    for (var step = 0; step < steps; step++) {

        var nextX, nextY;

        ////////////////////////////////////////////////////////////////////
        // even distribution

        nextX = x + stepX;
        nextY = y + random(-3*stepY, 3*stepY);


        ////////////////////////////////////////////////////////////////////
        // grid
        // nextX = x + stepX;
        // nextY = y + floor(random(-1,2)) * stepY;

        line(x, y, nextX, nextY);

        x = nextX;
        y = nextY;

    }
}

