var myTurtle;

function setup() {
  createCanvas(400, 400);
  noLoop();
  // frameRate(1);
  colorMode(HSB, 1, 1, 1, 1);
  background(0, 0, 0.9);
  strokeWeight(1);
  stroke(0, 0, 0, 0.5);
  noFill();

  myTurtle = new Turtle();
}

function draw() {
  myTurtle.penUp();
  // myTurtle.penDown();
  myTurtle.moveTo(height / 2, width / 2);
  myTurtle.moveForward(10);
  myTurtle.moveBackward(10);
  moveCircular(10, 20, drawGrowingCircles);
  myTurtle.turnRight(360 / 10 / 2);
  moveCircular(10, 40, circleOfCircles);
  moveCircular(40, 52, function(i) {
    push();
    fill(0, 1, 0.8);
    noStroke();
    gaps(i);
    noFill();
    pop();
  });
  moveCircular(80, 58, gaps);
  moveCircular(60, 74, gaps);
  moveCircular(10, 66, circleOfCircles2);
  finalPositionMarker();
}

function gaps(i) {
  if (i % 2 == 0) {
    circle(myX(), myY(), 2, 2);
  }
}

function circleOfCircles() {
  myTurtle.pushState();
  // myTurtle.penDown();
  moveCircular(10, 5, drawCircle);
  myTurtle.popState();
}
function circleOfCircles2() {
  myTurtle.pushState();
  // myTurtle.penDown();
  moveCircular(6, 4, function() {
    fill(0, 1, 0.8);
    noStroke();
    circle(myX(), myY(), 1, 1);
    noFill();
  });
  myTurtle.popState();
}

function drawCircle() {
  circle(myX(), myY(), 2, 2);
}

function drawGrowingCircles() {
  myTurtle.pushState();
  // myTurtle.turnLeft(20);
  for (let i = 0; i < 5; i++) {
    circle(myX(), myY(), i, i);
    myTurtle.moveForward(10 + i);
  }
  myTurtle.popState();
}

function moveCircular(n, s, callback) {
  myTurtle.pushState();
  for (let i = 0; i < n; i++) {
    myTurtle.turnRight(360 / n);
    myTurtle.moveForward(s);
    callback(i);
    myTurtle.moveBackward(s);
  }
  myTurtle.popState();
}

function finalPositionMarker() {
  fill(0, 1, 0.8);
  noStroke();
  circle(myX(), myY(), 2, 2);
}

function myX() {
  return myTurtle.x;
}
function myY() {
  return myTurtle.y;
}

