let img;
const canvasWidth = 800;
const canvasHeight = 600;

let reds = [];
let greens = [];
let blues = [];

function preload() {
    img = loadImage("https://raw.githubusercontent.com/annagarbier/compform/master/blog_800_01.png");
    // img = loadImage("https://raw.githubusercontent.com/annagarbier/compform/master/blog_800_02.png");
}

function setup() {
    createCanvas(canvasWidth, canvasHeight);
    pixelDensity(1);
    rectMode(CENTER);
}

function draw() {
    noStroke();

    var spacing = canvasWidth / img.width;
    for (var y = 0; y < img.height; y++) {
        for (var x = 0; x < img.width; x++) {
            var in_color = img.get(x, y);
            const r = in_color[0];
            const g = in_color[1];
            const b = in_color[2];

            let a = map(in_color[3], 0, 255, 0, 1);
            if (a > .5) {
                fill(r, g, b, 50);
                ellipse(
                    x * spacing + spacing * .5,
                    y * spacing + spacing * .5,
                    1, 1
                );
            }

            if (r > g + 25 && r > b + 25) {
                reds.push([x, y]);
            } else if (g > r + 25 && g > b + 25) {
                greens.push([x, y]);
            } else if (b > r + 25 && b > g + 25) {
                blues.push([x, y]);
            }

        }
    }

    fillByPrimaryColor(reds);
    fillByPrimaryColor(greens);
    fillByPrimaryColor(blues);
    noLoop();
}

function fillByPrimaryColor(_c) {
    push();
    let thisColorArray = _c;
    if (thisColorArray === reds) {
        fill(244, 65, 65);
        stroke(244, 65, 65);
    } else if (thisColorArray === greens) {
        fill(52, 155, 5);
        stroke(52, 155, 5);
    } else if (thisColorArray === blues) {
        fill(66, 134, 244);
        stroke(66, 134, 244);
    }

    for (let i = 0; i < thisColorArray.length; i++) {
        rect(
            thisColorArray[i][0],
            thisColorArray[i][1],
            2, 2
        )
    }
    pop();
}

