            ellipse(circles[i].x, circles[i].y, circles[i].circleSize);

            circles[i].x += circles[i].xSpeed;
            circles[i].y += circles[i].ySpeed;
            //check collisions 
            let r = circles[i].circleSize / 2;
            if ((circles[i].x < r) || (circles[i].x > width - r)) {
                circles[i].xSpeed = -circles[i].xSpeed;
            }
            if ((circles[i].y < r) || (circles[i].y > height - r)) {
                circles[i].ySpeed = -circles[i].ySpeed;
            }
            //check circles compared to one another 
            for (let j = 0; j < circles.length; j++) {
                if (j != i) {
                    let distance = dist(circles[i].x, circles[i].y, circles[j].x, circles[j].y);
                    // console.log(distance);
                    if (distance <= circles[i].circleSize) {
                        console.log("play sound");
                        mySounds[i].play();
                    }

                }
            }
        }
    }
}



