  rows = h / scale;

  cam = createCapture(VIDEO);
  cam.size(cols, rows);

  for (let x = 0; x < cam.width; x++) {
    terrain[x] = [];
    for (let y = 0; y < cam.height; y++) {
      terrain[x][y] = 0;
    }
  }
