class Bar {
  constructor(x, y, rot, size, length, rotspeed) {
    this.x = x;
    this.y = y;
    this.speedY = 10;
    this.size = size;
    this.offset = random(360);
    this.length = length;
    this.loffset = random(360);
    this.rotspeed = rotspeed;
    this.col1 = color(random(100), 65, 80);
    this.col2 = color((hue(this.col1) + 33) % 100, 65, 80);
    this.col3 = color((hue(this.col2) + 33) % 100, 65, 80);
    this.rot = rot;
  }

  show() {
    stroke(255);
    noStroke(); //strokeWeight(4);
    push();
    translate(this.x, this.y);
    rotate(this.rot + millis() * slider.value() * 0.1);
    //rotate(this.rot + millis() * this.rotspeed);
    var yoffset = sin(millis() * 0.2 + this.offset) * 100;
    var point1 = {
      x: 0,
      y: 0 + yoffset,
    };
    var point2 = {
      x: -sin(60) * this.size,
      y: cos(60) * this.size + yoffset,
    };
    var point3 = {
      x: 0,
      y: this.size + yoffset,
    };
    var point4 = {
      x: sin(60) * this.size,
      y: cos(60) * this.size + yoffset,
    }
    //quad(0, 0, -sin(PI / 3) * this.size, -cos(PI / 3) * this.size, 0, -this.size, sin(PI / 3) * this.size, -cos(PI / 3) * this.size);
    var newlength = this.length + sin(millis() * 0.2 + this.loffset) * 300;
    fill(this.col1);
    quad(point1.x, point1.y, point2.x, point2.y, point3.x, point3.y, point4.x, point4.y);
    fill(this.col2);
    quad(point2.x, point2.y, point2.x, point2.y + newlength, point3.x, point3.y + newlength, point3.x, point3.y);
    fill(this.col3);
    quad(point3.x, point3.y, point3.x, point3.y + newlength, point4.x, point4.y + newlength, point4.x, point4.y);
    pop();
  }
}

const infile = './data/first_lines.txt';

// PARSE INPUT DATA /////////////////////////////////////////////////////
function readDatasheet(file) {
    const raw_file = new XMLHttpRequest();
    let file_contents = '';
    raw_file.open("GET", file, false);
    raw_file.onreadystatechange = function () {
        if(raw_file.readyState === 4) {
            if(raw_file.status === 200 || raw_file.status == 0) {
                file_contents = raw_file.responseText;
            }
        }
    }
    raw_file.send(null);
    return file_contents;
}

function parseDatasheet() {
    const data = [];
    const datasheet = infile;
    const lines = readDatasheet(datasheet).split('\n');
    for (let i = 0; i < lines.length; i++) {
        data.push(lines[i]);
    }

    return data;
}


