class Hand {
    constructor(_x, _y, _img) {
        this.pos = {
            x: _x,
            y: _y,
        },
        this.img = _img,
        this.tip = {
            x: _x - .5,
            y: _y + 73
        },
        this.h = 800,
        this.w = .574 * this.h
    }

    lerpTo(_x, _y, _rand_mag, _penDown, _ptgroup) {
        this.penDown(_penDown, _ptgroup);

        let rand = {
            x: random(_rand_mag),
            y: random(- _rand_mag, _rand_mag)
        };

        const end_point_fixed = {
            x: _x + .5,
            y: _y - 73
        }

        this.pos.x = lerp(this.pos.x, end_point_fixed.x, 0.05) + rand.x;
        this.pos.y = lerp(this.pos.y, end_point_fixed.y, 0.05) + rand.y;

        this.tip = {
            x: this.pos.x - .5,
            y: this.pos.y + 73
        };
    }

    penDown(_isDown, _ptgroup) {
        // Points in line
        if (_isDown) {
            line_points[_ptgroup].push({x: this.tip.x, y: this.tip.y});
        }

        // Connect points
        strokeWeight(2.5);
        stroke(50);
        for (let i = 0; i < line_points.length; i++) {
            for (let p = 1; p < line_points[i].length; p++) {
                stroke(50);
                line(
                    line_points[i][p - 1].x, line_points[i][p - 1].y,
                    line_points[i][p].x, line_points[i][p].y,
                )
            }
        }
    }
}

class Circle {
  constructor(_x, _y, _diam) {
    this.x = _x,
    this.y = _y,
    this.diam = _diam
  }

  fall() {
    if (this.y < 500 - 10) {
      this.y += 8;
    } else if (this.y < 492) {
      this.y += 1;
      for (let i = 0; i < line_points.length; i++) {
        for (let p = 1; p < line_points[i].length; p++) {
          if (abs(line_points[i][p].x - C.x) < 10) {
            line_points[i][p].y += 1;
          }
        }
      }
    }
    if (this.y === 500 - 10) {
      if (!impact_played) {
        impact.play();
        impact_played = true;
      }
    }
  }

  display() {
    push();
    noStroke();
    fill(50);
    ellipse(this.x, this.y, this.diam, this.diam);
    pop();
  }
}

