function drawBrownianPath1D(x, y, stepX, stepY, steps) {
    for (var step = 0; step < steps; step++) {

        var nextX, nextY;

        ////////////////////////////////////////////////////////////////////
        // even distribution

        nextX = x + stepX;
        nextY = y + random(-0.5*stepY,0.5*stepY);


        ////////////////////////////////////////////////////////////////////
        // grid
        // nextX = x + stepX;
        // nextY = y + floor(random(-1,2)) * stepY;
        fill(random(100),random(210),250,125);
        //line(x, y, nextX, nextY);
        quad(x,y,x,height,nextX,height,nextX,nextY);
        fill(170,210,250);
        x = nextX;
        y = nextY;

    }
}
function drawBrownianPath2D(x, y, stepX, stepY, steps) {
    for (var step = 0; step < steps; step++) {

        var nextX, nextY;

        ////////////////////////////////////////////////////////////////////
        // even distribution

        nextX = x + stepX;
