let hexWidth;
let hexHeight;

let Points = [];

function setup () {
    createCanvas(canvasAndBorder.w, canvasAndBorder.h);
    rowHeight = Math.floor(canvas.h / numRows);
    colWidth = Math.floor(canvas.w / numCols);

    background(50, 50, 100, 50);
    stroke(50, 50, 100, 100);

    strokeWeight(1);

    translate(19, 19);
