class Point {
    constructor(_x, _y) {
        this.pos = {
            x: _x,
            y: _y
        }
    }

    displayAsEllipse() {
        ellipse(this.pos.x, this.pos.y, 3, 3);
    }
}

function setPoints() {
    push();
    for (let y = 0; y < canvas.h; y += rowHeight) {
        let newPoint;
        let n = 0;
        // console.log(canvas.w / 2, canvas.h / 2);
        if (y % (2 * rowHeight) === 0) {
            for (let x = 0; x < canvas.w - colWidth; x += colWidth) {
                n = noise(x + colWidth / 2 * .01, y * .01);
                // n = 0
                newPoint = new Point(x + colWidth / 2 + 15 * n, y + 15 * n);
                Points.push(newPoint);
            }
        } else {
            for (let x = 0; x < canvas.w; x += colWidth) {
                n = noise(x * .01, y * .01);
                // n = 0;
                newPoint = new Point(x + 15 * n, y + 15 * n);
                Points.push(newPoint);
            }
        }
    }
    pop();
}

function drawPoints() {
    for (let i = 0; i < Points.length; i++) {
        Points[i].displayAsEllipse();
    }
}

function connectPointsHorizontal() {
    for (let i = 0; i < Points.length - 1; i++) {
        if (Points[i].pos.x < canvas.w - colWidth) {
            line(
                Points[i].pos.x, Points[i].pos.y,
                Points[i + 1].pos.x, Points[i + 1].pos.y
            )
        }
    }
}

function connectPointsVerticalRight() {
    for (let i = 0; i < Points.length - numCols - 1; i++) {
        if (
            Points[i].pos.x < canvas.w - colWidth / 4 &&
            Points[i].pos.y < canvas.h - rowHeight / 2
        ) {
            line(
                Points[i].pos.x, Points[i].pos.y,
                Points[i + numCols + 1].pos.x, Points[i + numCols + 1].pos.y
            )
        }
    }
}

function connectPointsVerticalLeft() {
    for (let i = 0; i < Points.length - numCols; i++) {
        if (
            Points[i].pos.x >= colWidth / 2 &&
            Points[i].pos.y < canvas.h - rowHeight / 2
        ) {
            line(
                Points[i].pos.x, Points[i].pos.y,
                Points[i + numCols].pos.x, Points[i + numCols].pos.y
            )
        }
    }
}

function fillCircles() {
    push();
    let diam = colWidth;
    for (i = 0; i < Points.length - numCols - 1; i++) {
        if (
            Points[i].pos.x > noise(Points[i].pos.x, Points[i].pos.y, 1) * 50 &&
            Points[i].pos.y > noise(Points[i].pos.x, Points[i].pos.y, 2) * 50 &&
            Points[i].pos.x < canvas.w - noise(Points[i].pos.x, Points[i].pos.y, 2) * 50 &&
            Points[i].pos.y < canvas.h - noise(Points[i].pos.x, Points[i].pos.y, 2) * 50
        ) {
            noStroke();
            // console.log(Points[i]);
            let n = noise(Points[i].pos.x * .01, Points[i].pos.y * .01, frameCount * .005);
            if (n > .3) {
                fill(50, 50, 60, n * 255);
                ellipse(Points[i].pos.x, Points[i].pos.y, diam, diam);
            } 
        }
    }
    pop();
}

