function draw() {
    myTurtle.penUp();

    stroke(random(220, 250), random(220, 250), random(220, 250), .2);
    let startX = random(-50, 0);
    let startY = random(200, 250);
    for (let i = 0; i < 10; i++) {
        drawWave(startX, startY);
        drawBranch(100);

        startX += random(50, 150);
        startY += random(-10, 50);
        myTurtle.penUp();
    }

    stroke(255, 255, 255, .3);
    startX = -200;
    startY = random(300, 340);
    for (let i = 0; i < 20; i++) {
        drawWave(startX, startY);

        //inncriment range 
        startX += random(30, 50);
        startY += random(-10, 20);
        myTurtle.penUp();
    }
    //draw blue backgorund strip 
    stroke(255, 255, 255, .3);
    startX = -200;
    startY = random(320, 350);
    for (let i = 0; i < 20; i++) {
        drawWave(startX, startY);

        //inncriment range 
        startX += random(30, 50);
        startY += random(-5, 15);
        myTurtle.penUp();
    }

    //draw wave row two 
    stroke(255, 255, 255, .3);
    startX = random(-100, -50);
    startY = random(350, 450);
    for (let i = 0; i < 10; i++) {
        drawWave(startX, startY);
        drawBranch(100);
        startX += random(50, 150);
        startY += random(-10, 50);
        myTurtle.penUp();
    }

}

function drawWave(startX, startY) {
    for (let i = 0; i < 16; i++) {
        startX += random(-10, 10);
        myTurtle.moveTo(startX, startY);
        myTurtle.turnTo(random(-3, 3));
        myTurtle.penDown();
        drawBlueWater(100);
        myTurtle.penUp();
    }
    myTurtle.turnTo(0);
    myTurtle.penDown();
    myTurtle.moveTo(startX, startY);

}



function drawBlueWater(length) {
    push();
    strokeWeight(10);
    stroke(0, int(random(100)), int(random(150, 250)), .1);
    if (length < 35) {
        return;
    }
    // draw this branch
    strokeWeight(length * .3);

    stroke(0, random(100), random(150, 250), .1);
    myTurtle.moveForward(length * random(.4, .5));
    //curve of main wave 
    pop();
    myTurtle.turnLeft(random(2, 10));
    // myTurtle.turnLeft();
    myTurtle.moveForward(length * random(.4, .5));
    // left child
    myTurtle.pushState();
    // stroke(255, 255, 255, .5);
    myTurtle.turnLeft(20);
    drawBlueWater(length * 0.75);
    drawBlueWater(length * .6);
    myTurtle.popState();
    // right child
    myTurtle.pushState();
    myTurtle.turnRight(random(2, 5));
    //this one effects how much fluff there is 
    drawBlueWater(length * .7);
    // drawBlueWater(length * random(.5));
    myTurtle.popState();

}



function drawBranch(length) {
    if (length < 1.5) {
        return;
    }
    // draw this branch
    strokeWeight(1);
    stroke(255, 255, 255, .5);
    myTurtle.moveForward(length * .45);
    //curve of main wave 
    myTurtle.turnLeft(8);
    stroke(255, 255, 255, .5);
    myTurtle.moveForward(length * .45);
    // left child
    myTurtle.pushState();
    stroke(255, 255, 255, .5);
    myTurtle.turnLeft(random(20, 40));
    stroke(255, 255, 255, .5);
    drawBranch(length * 0.75);
    //drawBranch(length * random(.5, .9));
    myTurtle.popState();
    // right child
    myTurtle.pushState();
    myTurtle.turnRight(random(2, 5));
    //this one effects how much fluff there is 
    drawBranch(length * 0.7);
    //drawBranch(length * random(.5, .9));
    myTurtle.popState();
}

