let synth = new Tone.PolySynth(6, Tone.Synth).toMaster();

synth.set("detune", -600);

let chords = [cTriad(), dTriad(), eTriad(), fTriad(), gTriad()];
let degree = 0;

window.onkeypress = function(event) {
  let x = event.which || event.keyCode;

  if (x == 49) {
    cTriad();
  } else if (x == 50) {
    dTriad();
  } else if (x == 51) {
    eTriad();
  } else if (x == 52) {
    fTriad();
  } else if (x == 53) {
    gTriad();
  } else if (x == 54) {
    bTriad();
  }
};

