var myTurtle;

function setup() {
	createCanvas(500, 500);
	noFill();
	stroke(255,50);
	background(0);
	noLoop();
    strokeWeight(1);

	myTurtle = new Turtle();
}



function draw() {
	myTurtle.penUp();
	myTurtle.moveTo(250, 250);
	myTurtle.penDown();

	for (var i = 0; i < 500; i++) {

		myTurtle.moveForward(i*0.1-25);
		myTurtle.turnLeft(5);
        myTurtle.moveForward(i*0.1+25);
        myTurtle.turnLeft(20);   

		drawBranch();

	}
}

function drawBranch() {
	myTurtle.pushState();

	for (i = 0; i < 50; i++) {
		myTurtle.moveForward(tan(i+20));
		myTurtle.turnLeft(random(20,22));
        myTurtle.moveForward(tan(i+50));
		myTurtle.turnLeft(random(30,32));
             myTurtle.moveForward(tan(i+80));
		myTurtle.turnLeft(random(40,42));
	}

	myTurtle.popState();
}

