var center = new Point(250, 250);
var i=0;



var circlePath = new Path.Circle(new Point(380, 360), 100);
circlePath.strokeColor = 'black';
circlePath.fillColor = 'white';

circlePath.style = {
    strokeColor: '#f9cddf',


    fillColor: '#f9cddf',

    strokeWidth: 20,
    strokeCap : 'round',
    dashArray: [10, 50],


    shadowColor: new Color(0, 0, 0, .5),
    shadowBlur: 12,
    shadowOffset: new Point(5, 5),
}
circlePath.opacity = 0.2;

var clones = 15;
var angle = 360 / clones;

for(var i = 0; i < clones; i++) {
	var clonedPath = circlePath.clone();
	clonedPath.rotate(angle * i, circlePath.bounds.topLeft);
};

// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/p5.js
