function createSliders() {
    createP('Square size');
    sizeSlider = createSlider(50, 600, 400);

    createP('Square density');
    densitySlider = createSlider(1, 50, 37);
}

class Mark {
    constructor() {
        this.pts = []; // array of 4 x, y points

        let canvasCntr = {x: canvasWidth * .5, y: canvasHeight * .5};
        let metaSquareWidthHalved = sizeSlider.value() / 2;

        this.pts[0] = {
            x: canvasCntr.x + random(-metaSquareWidthHalved, metaSquareWidthHalved),
            y: canvasCntr.y + random(-metaSquareWidthHalved, metaSquareWidthHalved)
        }
        this.pts[1] = {
            x: this.pts[0].x + random(3, maxCenterSize),
            y: this.pts[0].y + random(1, maxCenterSize / 3)
        }
        this.pts[2] = {
            x: this.pts[0].x + random(3, maxCenterSize),
            y: this.pts[0].y + random(3, maxCenterSize)
        }
        this.pts[3] = {
            x: this.pts[0].x + random(1, maxCenterSize / 3),
            y: this.pts[0].y + random(3, maxCenterSize)
        }
    }

    drawCenter() {
        quad(
            this.pts[0].x, this.pts[0].y,
            this.pts[1].x, this.pts[1].y,
            this.pts[2].x, this.pts[2].y,
            this.pts[3].x, this.pts[3].y
        );
    }

    drawSpoke(iStart, iEnd) {
        let steps = random(1, maxSpokeLength);
        let riseMultiplier = this.pts[iStart].y - this.pts[iEnd].y;
        let runMultiplier = this.pts[iStart].x - this.pts[iEnd].x;

        let rise = steps * riseMultiplier;
        let run = steps * runMultiplier;

        let startPt = {x: this.pts[iStart].x, y: this.pts[iStart].y};
        let endPt = {x: startPt.x + run, y: startPt.y + rise};

        line(startPt.x, startPt.y, endPt.x, endPt.y);
    }

    drawSelf() {
        this.drawCenter();
        this.drawSpoke(0, 1);
        this.drawSpoke(1, 2);
        this.drawSpoke(2, 3);
        this.drawSpoke(3, 0);
        this.drawSpoke(1, 0);
        this.drawSpoke(2, 1);
        this.drawSpoke(3, 2);
        this.drawSpoke(0, 3);
    }
}

function draw() {
    background(255);
    // noFill();
    // rect(1, 1, 798, 798);
    // fill(45, 45, 45);
    let marks = [];
    let numMarks = sizeSlider.value() * densitySlider.value();
    for (let i = 0; i < numMarks; i++) {
        marks[i] = new Mark();
        marks[i].drawSelf();
    }
}

