    // hint: the plus sign was crucial
    var offsetX = noise(frequency * i + noisePosition, 0) * amplitude * 10;
    var offsetY = noise(frequency * i + noisePosition, 1000) * amplitude * 10;

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

