class Shape {
  constructor(
    type = "triangle",
    cx = w / 2,
    cy = h / 2,
    width = 100,
    height = 100,
    rotation = 0,
    myFill = "#000000",
    myStroke = "none",
    myStrokeWidth = 2
  ) {
    this.type = type;
    this.cx = cx;
    this.cy = cy;
    this.width = width;
    this.height = height;
    this.rotation = rotation;
    this.myFill = myFill;
    this.myStroke = myStroke;
    this.myStrokeWidth = myStrokeWidth;
  }
  randomize() {
    // type
    const typesIndex = floor(random(types.length));
    this.type = types[typesIndex];
    // coordinates
    const x = (random(w) + random(w)) / 2;
    const y = (random(h) + random(h)) / 2;
    this.cx = x;
    this.cy = y;
    // size
    const sizesIndex = floor((random(sizes.length) + random(sizes.length)) / 2);
    const size = sizes[sizesIndex];
    let myHeight =
      (random(size * 0.01, size * 0.1) + random(size * 0.01, size * 0.1)) / 2;
    let myWidth = (size / height) * 150;
    this.width = myWidth;
    this.height = sizes[sizesIndex];
    // rotation
    const rotationsIndex = floor(random(rotations.length));
    this.rotation = rotations[rotationsIndex];
    // colors
    const colorsIndex = floor(
      min((random(colors.length), random(colors.length), random(colors.length)))
    );
    const myColor = colors[colorsIndex];
    const strokeOrFill = random(1) < 0.3;

    let fillColor = "none";
    let strokeColor = "none";
    if (strokeOrFill) {
      strokeColor = myColor;
    } else {
      fillColor = myColor;
    }
    this.myFill = fillColor;
    this.myStroke = strokeColor;
    this.myStrokeWidth = strokeWidthSlider.value();
  }
  draw() {
    if (this.type == "triangle") {
      drawTriangle(
        this.cx,
        this.cy,
        this.width,
        this.height,
        this.rotation,
        this.myFill,
        this.myStroke,
        this.myStrokeWidth
      );
    } else if (this.type == "rectangle") {
      drawRectangle(
        this.cx,
        this.cy,
        this.width,
        this.height,
        this.rotation,
        this.myFill,
        this.myStroke,
        this.myStrokeWidth
      );
    }
  }
}

class RandomShape extends Shape {
  constructor() {
    super();
    this.randomize();
  }
}

function drawTriangle(
  cx,
  cy,
  width,
  height,
  rotation,
  myFill,
  myStroke,
  myStrokeWidth
) {
  push();
  const size = height;
  const scale = size;
  translate(cx, cy);

  rotate(180);
  rotate(rotation);

  const x1 = 0 - 0.5 * scale;
  const x2 = 1 * scale - 0.5 * scale;
  const x3 = 0.5 * scale - 0.5 * scale;
  const y1 = 0 - 0.433 * scale;
  const y2 = 0 - 0.433 * scale;
  const y3 = 0.866 * scale - 0.433 * scale;

  if (myFill == "none") {
    // console.log("yes");
    noFill();
  } else {
    fill(myFill);
  }
  strokeWeight(myStrokeWidth);
  if (myStroke == "none") {
    noStroke();
  } else {
    stroke(myStroke);
  }

  triangle(x1, y1, x2, y2, x3, y3);
  // Check center
  // strokeWeight(10);
  // stroke(255, 0, 0);
  // point(0, 0);
  pop();
}

function drawRectangle(
  cx,
  cy,
  width,
  height,
  rotation,
  myFill,
  myStroke,
  myStrokeWidth
) {
  push();
  translate(cx, cy);
  rotate(rotation);

  if (myFill == "none") {
    noFill();
  } else {
    fill(myFill);
  }
  strokeWeight(myStrokeWidth);
  if (myStroke == "none") {
    noStroke();
  } else {
    stroke(myStroke);
  }

  rect(0, 0, height, width);
  pop();
}

