var myTurtle;
let carrier; // this is the oscillator we will hear
let modulator; // this oscillator will modulate the frequency of the carrier
let modMinFreq = 0;
// let modMaxFreq = 112;
let modMaxDepth = 15;
let modMinDepth = -15;

function setup() {
  colorMode(HSB, 1, 1, 1, 1);
  let cnv = createCanvas(500, 500);
