  window.modMaxFreqSlider = createSlider(0, 1000, 10);
  createP('Modulator Depth Range');
  window.modDepthRange = createSlider(0, 100, 5);
  createP('OSC component A');
  window.oscA = createSlider(0, 10, 0.1, 0.001);
  createP('OSC component B');
  window.oscB = createSlider(0, 10, 1, 0.001);
  createP('OSC component C');
