  myTurtle = new Turtle();
  frameRate(30);
  background(0, 0, 0.2);
  myTurtle.penUp();
  myTurtle.moveTo(250, 100);

  carrier = new p5.Oscillator('sine');
  carrier.amp(0); // set amplitude
  carrier.freq(carrierBaseFreq.value()); // set frequency
  carrier.start(); // start oscillating

  // try changing the type to 'square', 'sine' or 'triangle'
  modulator = new p5.Oscillator('sine');
  modulator.start();

  // add the modulator's output to modulate the carrier's frequency
  modulator.disconnect();
  carrier.freq(modulator);

}

function draw() {
  if (play) {
    myUpdate()
  }
}

function myUpdate() {
  // background(30, 0.2, 1);

  noFill();
  stroke(0, 0.2, 1, 0.5);
  strokeWeight(0.5);

  let x = sin(frameCount * 0.1) * 21 * oscA.value() +
  cos(frameCount * 0.01) * 12* oscB.value() +
  sin(frameCount * 0.001) * 32* oscC.value();

  let y = sin(frameCount * 0.001) * 21 * oscA.value() +
  cos(frameCount * 0.01) * 12* oscB.value() +
  sin(frameCount * 0.1) * 32* oscC.value();

  myTurtle.penDown();
  myTurtle.moveForward(5);
  myTurtle.turnRight(
    x
  );
  if (
    myTurtle.x > width ||
    myTurtle.x < 0 ||
    myTurtle.y > height ||
    myTurtle.y < 0
  ) {
    myTurtle.penUp();
    myTurtle.moveTo(random(height), random(width));
    myTurtle.penDown();
  }


  carrier.freq(carrierBaseFreq.value()); // set frequency
  var modMaxFreq = modMaxFreqSlider.value()

  let modFreq = map(x, -1, 1, modMinFreq, modMaxFreq);
  modulator.freq(modFreq);

  // change the amplitude of the modulator
  // negative amp reverses the sawtooth waveform, and sounds percussive
  //
  modMaxDepth = modDepthRange.value()
  modMinDepth = -modDepthRange.value()

  let modDepth = map(y, -1, 1, modMinDepth, modMaxDepth);
  modulator.amp(modDepth);
}

