var appleSpeed = 0;
const Y_AXIS = 1;
const X_AXIS = 2;
let b1, b2, c1, c2;

function setup() {
  createCanvas(700, 600);
  noLoop();

  //for loop creating the apples
  for (var a = 0; a < totalapples; a++) {
    apples[a] = new Apple();
  }
  b1 = color(random(256),random(256),random(256));
  b2 = color(random(256),random(256),random(256));

  noLoop();

}

function draw() {
  setGradient(0, 0, width, height, b1, b2, Y_AXIS);

  strokeWeight(2);
  stroke(random(30,255));
  translate(width / 2, height - 20);
  branch(0);

  //for loop to call the functions
  for (var a=0; a < apples.length; a++) {
    apples[a].render();
    apples[a].update();
    apples[a].initiate();
  }
}

function setGradient(x, y, w, h, c1, c2, axis) {
  noFill();

  if (axis === Y_AXIS) {
    // Top to bottom gradient
    for (let i = y; i <= y + h; i++) {
      let inter = map(i, y, y + h, 0, 1);
      let c = lerpColor(c1, c2, inter);
      stroke(c);
      line(x, i, x + w, i);
    }
  } else if (axis === X_AXIS) {
    // Left to right gradient
    for (let i = x; i <= x + w; i++) {
      let inter = map(i, x, x + w, 0, 1);
      let c = lerpColor(c1, c2, inter);
      stroke(c);
      line(i, y, i, y + h);
    }
  }
}

function branch(depth) {
  if (depth < 10) {
    line(0, 0, 0, -height / 6); // draw a line going up
    {
      translate(0, -height / 6); // move the space upwards      
      rotate(random(-0.2,0.2)); // random wiggle

      rotate(0.3); // rotate to the right
      scale(random(0.7,0.9)); // scale down

      push(); // now save the transform state
      branch(depth + 1); // start a new branch!
      pop(); // go back to saved state

      rotate(-0.6); // rotate back to the left 

      push(); // save state
      branch(depth + 1); // start a second new branch 
      pop(); // back to saved state
      //source of tree: https://editor.p5js.org/mtchl/sketches/ryyW2U5Fx
    }

  }
}

