  this.y = this.y + appleSpeed;

  //draws the ball
  this.render = function() {
    //fill('red');
    //ellipse(this.x, this.y, appleSize, appleSize);
  }
  //updates the position of the apples
  this.update = function() {
    this.y = this.y + appleSpeed;
  }

  //send apples back to top
  this.initiate = function() {
    if (this.y > height) {
      this.y = random(-600, 0);
    }
  }
}

