var amplitude_slider;
var timeSlider;
var frequencySlider;

var startX = 50;
var startY = 250;
var endX = 450;
var endY = 50;


function setup() {
    createCanvas(500, 300);


    createP('Amplitude');
    amplitude_slider = createSlider(0, 200, 120);

    createP('Time speed');
    timeSlider =  createSlider(0, 10, 2);

    createP('frequency');
    frequencySlider=  createSlider(0,10,1);

}


function draw() {
    background(50);
    ellipseMode(CENTER);

    var amplitude = amplitude_slider.value() ;
    var time = timeSlider.value() ;
    var freq = map (frequencySlider.value(),0,10,0.01,0.07 );



    noiseDetail(1, .5);



    fill(255);
    noStroke();
    let n;

    // study this loop. do you understand how the line of ellipses is created?
    for (i = 0; i < 1; i += .02) {
        var x = lerp(startX, endX, i);
        var y = lerp(startY, endY, i);
        // var offsetX = 0;
        // var offsetY = 0;


        var  offsetY = noise(x) * 0.2;

        n = noise(x * freq + millis()*time * -.001);
        var offsetX = n * amplitude;

        ellipse(x + offsetX, y + offsetY, 10, 10);
    }


}

