// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js

function setup() {
  createCanvas(700, 700);
  noLoop();
}


function draw() {
  background('#1C151D');
  bigCircles();
  centerC(width/2,height/2);
}



function bigCircles(){
  strokeWeight(2.5);
  r = random(100);
  xW = width/2;
  yH = height/2;


  for (let i=0; i<15; i+=7){
    for (let j=0; j<15; j+=7){
      noFill();
      stroke('#EEEAE7');
      ellipse(xW, yH, i*43, j*43);

      ellipse(700-r,yH, i*r, j*r);
      ellipse(r,yH, i*r, j*r);
      ellipse(xW,r, i*r, j*r);
      ellipse(xW,700-r, i*r, j*r);
    }
  }
}


function centerC(x,y) {  
  noStroke();
  r = random(50);

  for (let i=0; i<15; i++){
    for (let j=0; j<15; j++){
    fill(187,192,213,1);
    ellipse(xW, yH, i*r, j*r);
    }
  }
}

