function setup() {
    createCanvas(400, 400);
    frameRate(30);
    noStroke()
    angleMode(DEGREES);
}

function draw() {
    background("black");

    for (let i = 0; i < 15; i++) {

        let speed
        if (i % 3 == 0) {
            fill(237, 64, 57);
            speed = 4
        } else if (i % 3 == 1) {
            fill(32, 161, 171)
            speed = 5
        } else {
            fill(253, 173, 19)
            speed = 6
        }

        let a = map(frameCount, 0, 20, 0, 2 * PI) * speed
        let r = 100 * sin(a)
        let x = 200 + r * sin((i + frameCount / 20) * 24)
        let y = 200 + r * cos((i + frameCount / 20) * 24)
        circle(x, y, 13);
    }
}

