function drawHearts(xOffset, yOffset, size, spacing) {
  for (let i = 0; i < 20; i++) {
    for (let j = 0; j < 20; j++) {
      let x = xOffset + i * (size + spacing)+noise(i)*0.03;
      let y = yOffset + j * (size + spacing)- noise(j)*0.01;
      drawHeart(x, y, size);
    }
  }
}

function drawHeart(x, y, size) {
  beginShape();
  vertex(x, y + size / 4);
  bezierVertex(x - size / 2, y - size / 2, x - size, y, x, y + size);
  bezierVertex(x + size, y, x + size / 2, y - size / 2, x, y + size / 4);
  endShape(CLOSE);
}

