function gridNoiseCullDithered(grid_size, w, h, frequency, threshold, dither) {
  let points = placePointsGrid(grid_size, w, h);
  return noiseCull(points, frequency, threshold, dither);
}

function placePointsGrid(grid_size, w, h) {
  let points = [];
  for (let y = 0; y < grid_size; y++) {
    for (let x = 0; x < grid_size; x++) {
      points.push({
        x: ((x + 0.5) / grid_size) * w,
        y: ((y + 0.5) / grid_size) * h,
      });
    }
  }
  return points;
}

function noiseCull(points, frequency, threshold, dither) {
  let outPoints = [];
  for (let i = 0; i < points.length; i++) {
    let p = points[i];
    if (noise(p.x * frequency, p.y * frequency) < threshold + random() * dither) {
      outPoints.push(p);
    }
  }
  return outPoints;
}

