let spacing = spacingSlider.value(); // Assuming this value controls the spacing directly
  let adjustedGridSize = width / spacing; // Calculate how many points fit into the width based on spacing
  let totalPoints = adjustedGridSize * adjustedGridSize; // Assuming a square grid for simplicity

  let points = gridNoiseCullDithered(sqrt(totalPoints), width, height, 0.01, 0.33, 0.33);
  drawPoints(points);
}

function drawPoints(points) {
  // Get color values from sliders
  let hueStart = colorStartSlider.value();
  let hueEnd = colorEndSlider.value();
  let colorStart = color(hueStart, 100, 100);
  let colorEnd = color(hueEnd, 100, 100);

  // Use the scaleSlider value to determine the size of the circles
  let scaleFactor = scaleSlider.value() / 20; // Assuming the max value of the slider is 100

  for (let i = 0; i < points.length; i++) {
    let sizeNoise = noise(points[i].x * 0.05, points[i].y * 0.05);
    let circleSize = map(sizeNoise, 0, 1, POINT_SIZE / 2, POINT_SIZE * 2) * scaleFactor;

    let lerpedColor = lerpColor(colorStart, colorEnd, sizeNoise);
    fill(lerpedColor);
    ellipse(points[i].x, points[i].y, circleSize, circleSize);
  }
}

