  let points = [];
  for (let y = 0; y < grid_size; y++) {
    for (let x = 0; x < grid_size; x++) {
      points.push({
        x: ((x + 0.5) / grid_size) * w,
        y: ((y + 0.5) / grid_size) * h,
      });
    }
  }
  return points;
}

