  const numLines = 50; 
  const lineLength = 75;
  const angleIncrement = TWO_PI / numLines;

  for (let i = 0; i < numLines; i++) {
    const angle = i * angleIncrement;
    const x1 = mouseX + cos(angle) * lineLength;
    const y1 = mouseY + sin(angle) * lineLength;
    line(mouseX, mouseY, x1, y1);
  }

  // Draw circle for mouse cursor
  fill('#49443c'); 
  ellipse(mouseX, mouseY, 30); 

  // Limit the trail length
  if (trail.length > 100) {
    trail.shift();
  }
}

