      };
    }
  }

  createTriangle(x, y, s) {
    const x2 = x + s / 2;
    const y2 = y - s * sin(60);
    const x3 = x + s;
    triangle(x, y, x2, y2, x3, y);
  }

  createStripe(r, g, b, inst) {
    let emitter = [];
    for (let i = 0; i < inst; i++) {
      emitter[i] = new Emitter(this.x0 - 2 * i, this.y0, r, g, b);
    }
    return emitter;
  }

  drawStripe(color, x0, y0, delta_x, delta_y) {
    const wind = createVector(-1, 0);
    for (let i = 0; i < color.length; i++) {
      color[i].applyForce(wind);

      color[i].emit(1);
      color[i].position.x = x0 - 5 * i + delta_x;
      color[i].position.y = 20 * sin(degrees(color[i].position.x) * 0.01 + 175) + 250 + delta_y;

      color[i].show();
      color[i].update();
    }
  }
}

