let xPoints = []; //Declare as an empty array so that it can be filled
let yPoints = [];
let spacing; // Change this to adjust the spacing
let xSpeeds = [];
let ySpeeds = [];

const params = {
  Stroke: 50,
  Pixelation: 100,
};

const pane = new Tweakpane.Pane();

pane.addInput(params, "Stroke", { min: 20, max: 200 });
