function generateModel(...args) {
    const words = args.toString().split("\n");
    const model = {};
    //console.log(words);
    for (let i = 0; i < words.length - 1; i++) {
        //https://www.30secondsofcode.org/js/s/string-is-uppercase-or-lowercase/ learned how to check if string is uppercase
        //regex expresison from chatGPT, matches uppercase + :
        const isSpeaker = str => /^[A-Z]+[:=]$/.test(str);

        if (isSpeaker(words[i])) {
            const speaker = words[i];
            var next_line=words[i+1];
            for (let j = 1; j<20; j++) {
                if(isSpeaker(words[i+1+j])) break;
                next_line+=`\n` + words[i+1+j];

            }

            if (!model[speaker]) {
                model[speaker] = [];
            }
            model[speaker].push(next_line);
        }
    }
    return model;
}

function generateScript(model, first_speaker){
    first_speaker = first_speaker ?? pick(Object.keys(model));
    let output_text = `\n ${first_speaker} \n ${model[first_speaker]}`;
    let current_speaker = first_speaker;
    for (let i = 0; i < 5; i++) {
        // verify that the current word is in the model
      if (!model[current_speaker]) break;
        current_speaker = pick(Object.keys(model));
        output_text += `\n ${current_speaker} \n ${model[current_speaker]}`;
    }
    console.log(output_text);
    return output_text;
}

function pick(array) {
   // console.log(array)
    const index = Math.floor(Math.random() * array.length);
    return array[index];
}

