let numClocks = 800; // Number of clocks
let clockRadius = 20; // Radius of each clock
let noiseScale = 0.01; // Scale for Perlin noise
let angles = []; // Array to store angles for each clock
let anglesChange = []; // Array to store angle changes for each clock
let doubleLengthPointers = []; // Array to store whether each pointer is double length
let frameCounter = 0;
let changeInterval = 1 * 30; // Change every 1 seconds (1 * 30 frames, assuming frame rate is 30fps)

function setup() {
  createCanvas(1200, 1200);
  frameRate(30);

  // Initialize angles and angle changes for each clock
  for (let i = 0; i < numClocks; i++) {
    angles.push(0);
    anglesChange.push(0);
    doubleLengthPointers.push(false); // Initially, no pointers are double length
  }
}

function draw() {
  background(20);

  // Update angle changes based on Perlin noise
  for (let i = 0; i < numClocks; i++) {
    let xOff = map(i, 0, numClocks, 0, 2); // Offset for Perlin noise
    anglesChange[i] = map(noise(xOff, frameCount * noiseScale), 0, 1, -0.1, 0.1); // Map noise to angle change
  }

  // Update angles based on angle changes
  for (let i = 0; i < numClocks; i++) {
    let angle = angles[i];
    let xPos = (i % 5) * 45 + 20 + clockRadius; // Calculate x position of clock
    let yPos = Math.floor(i / 5) * 45 + 20 + clockRadius; // Calculate y position of clock

    // Calculate angle towards mouse position
    let angleToMouse = atan2(mouseY - yPos, mouseX - xPos);
    // Calculate angle difference between clock and mouse
    let angleDiff = atan2(sin(angleToMouse - angle), cos(angleToMouse - angle));
    // Adjust angle if the mouse is within a certain radius of the clock
    let maxDist = 50; // Maximum distance for mouse effect
    let maxChange =1; // Maximum change in angle
    let distToMouse = dist(xPos, yPos, mouseX, mouseY); // Corrected distance calculation
    if (distToMouse < maxDist) {
      let change = map(distToMouse, 0, maxDist, maxChange, 0); // Change angle based on distance
      angles[i] += change * cos(angleDiff); // Adjust angle towards mouse
    } else {
      // If mouse is not close enough, revert to noise-based movement
      angles[i] += anglesChange[i]; // Update angle with noise-based change
    }
    angles[i] = angles[i] % TWO_PI; // Ensure angle remains within [0, TWO_PI]
  }

  // Draw clocks
  for (let y = 20; y < 1150; y += 40) {
    for (let x = 20; x < 1150; x += 40) {
      let index = floor((x / 45) + (y / 45) * (width / 45)); // Calculate index of clock and round to integer

      // Calculate position and draw clock
      let xPos = x + clockRadius;
      let yPos = y + clockRadius;
      drawClock(xPos, yPos, angles[index], doubleLengthPointers[index]); // Pass whether the pointer should be double length
    }
  }

  // Check if it's time to change pointer lengths
  if (frameCounter % changeInterval == 0) {
    changePointerLengths();
    // saveImage(); 
  }
  frameCounter++;
}

function drawClock(x, y, angle, isDoubleLength) {
  // Draw clock face
  noFill();
  stroke(20);
  ellipse(x, y, clockRadius * 2);

  // Draw second hand
  stroke(255, 255, 255);
  let secondHandLength = isDoubleLength ? clockRadius : clockRadius * 1; // Adjust length based on whether it's double length
  let secondX = x + secondHandLength * cos(angle);
  let secondY = y + secondHandLength * sin(angle);
  line(x, y, secondX, secondY);

  if (isDoubleLength) { // Draw the second line only for double length pointers
    let secondX2 = x - secondHandLength * cos(angle)*0.9;
    let secondY2 = y - secondHandLength * sin(angle)*0.9;
    line(x, y, secondX2, secondY2);
  }
}

