class Dot {
  constructor(frequency, speed, r, g, b, a) {
    this.frequency = frequency;
    this.moveXtime = 0;
    this.moveYtime = 0;
    this.w = (random(width / 8, width) + random(width / 8, width)) / 1.5;
    this.h = (random(height / 8, height) + random(height / 8, height)) / 1.5;
    this.speed = noise(speed * frequency) * 5;
    this.colorSpeed = speed;
    this.r = r;
    this.g = g;
    this.b = b;
    this.a = a;
  }

  make() {
    //where am i putting the dot
    this.xStart = noise(this.frequency) * this.w;
    this.yStart = noise(this.frequency) * this.h;
    this.diameter = noise(this.frequency) * 20;

    // this.moveX2();
    this.x = this.xStart + noise(this.frequency) * this.moveXtime;
    // this.moveY2();
    this.y = this.yStart + noise(this.frequency) * this.moveYtime;

    //draw the dot
    this.circleColor = color(this.r, this.g, this.b, this.a);
    noStroke();
    fill(this.circleColor);
    ellipse(this.x, this.y, this.diameter, this.diameter);
  }

  moveX1() {
    if (this.x < 0) {
      this.speed = noise(abs(this.speed) * this.frequency) * 2;
    } else if (this.x > 0 && this.x < width) {
      if (random() > 0.5) {
        this.speed = noise(abs(this.speed) * this.frequency) * 2;
      } else {
        this.speed = -2 * noise(abs(this.speed) * this.frequency);
      }
    } else if (this.x > width) {
      this.speed = -2 * noise(abs(this.speed) * this.frequency);
    }
    this.moveXtime += this.speed;
  }
  moveY1() {
    if (this.y < 0) {
      this.speed = noise(abs(this.speed) * this.frequency) * 2;
    } else if (this.y > 0 && this.y < width) {
      if (random() > 0.5) {
        this.speed = noise(abs(this.speed) * this.frequency) * 2;
      } else {
        this.speed = -2 * noise(abs(this.speed) * this.frequency);
      }
    } else if (this.y > width) {
      this.speed = -2 * noise(abs(this.speed) * this.frequency);
    }
    this.moveYtime += this.speed;
  }

  moveX2() {
    if (this.x < 0) {
      this.speed = noise(abs(this.speed) * this.frequency) * 2;
    } else if (this.x > width) {
      this.speed = -2 * noise(abs(this.speed) * this.frequency);
    }
    this.moveXtime += this.speed;
  }
  moveY2() {
    if (this.y < 0) {
      this.speed = noise(abs(this.speed) * this.frequency) * 2;
    } else if (this.y > width) {
      this.speed = -2 * noise(abs(this.speed) * this.frequency);
    }
    this.moveYtime += this.speed;
  }

  moveX3() {
    if (random() > 0.5) {
      if (this.x < 0) {
        this.speed = noise(abs(this.speed) * this.frequency) * 3;
      } else if (this.x > width) {
        this.speed = -noise(abs(this.speed) * this.frequency) * 3;
      }
    } else {
      this.moveXtime += this.speed;
    }
  }
  moveY3() {
    if (random() > 0.5) {
      if (this.y < 0) {
        this.speed = noise(abs(this.speed) * this.frequency) * 3;
      } else if (this.y > width) {
        this.speed = -noise(abs(this.speed) * this.frequency) * 3;
      }
    } else {
      this.moveYtime += this.speed;
    }
  }

  changeR() {
    if (this.r < 1) {
      this.colorSpeed = abs(this.colorSpeed);
    } else if (this.r > 255) {
      this.colorSpeed = -abs(this.colorSpeed);
    }
    this.r += this.colorSpeed;
    this.circleColor = color(this.r, this.g, this.b, this.a);
  }
  changeG() {
    if (this.g < 1) {
      this.colorSpeed = abs(this.colorSpeed);
    } else if (this.g > 255) {
      this.colorSpeed = -abs(this.colorSpeed);
    }
    this.g += this.colorSpeed;
    this.circleColor = color(this.r, this.g, this.b, this.a);
  }
  changeB() {
    if (this.b < 1) {
      this.colorSpeed = abs(this.colorSpeed);
    } else if (this.b > 255) {
      this.colorSpeed = -abs(this.colorSpeed);
    }
    this.b += this.colorSpeed;
    this.circleColor = color(this.r, this.g, this.b, this.a);
  }
  changeA() {
    if (this.a < 1) {
      this.colorSpeed = abs(this.colorSpeed);
    } else if (this.a > 255) {
      this.colorSpeed = -abs(this.colorSpeed);
    }
    this.a += this.colorSpeed;
    this.circleColor = color(this.r, this.g, this.b, this.a);
  }
}

