let myTurtle;

function setup() {
  createCanvas(windowWidth / 2, windowHeight / 2);
  noFill();
  stroke(255);

  background(0);
  angleMode(DEGREES);
  myTurtle = new Turtle();
  drawFanBranches(150, 180);

  // Draw the triangle
  let triangleSize = 200;
  let triangleX = width / 2;
  let triangleY = height * 1.5 - triangleSize;
  fill(0, 0, 255, 200);
  triangle(
    triangleX - triangleSize / 4,
    triangleY,
    triangleX + triangleSize / 4,
    triangleY,
    triangleX,
    triangleY - triangleSize
  );
}

function drawFanBranches(length, numBranches) {
  // Set the initial angle for the first branch
  let initialAngle = -180;
  // Angle increment between each branch
  let angleIncrement = 2;

  // Draw each branch in a fan-like pattern
  for (let i = 0; i < numBranches; i++) {
    // Move the turtle to the center of the canvas
    myTurtle.penUp();
    myTurtle.moveTo(width / 2, height / 1);

    // Set the angle for the current branch
    let branchAngle = initialAngle + i * angleIncrement;

    // Draw the branch
    myTurtle.turnTo(branchAngle);
    myTurtle.penDown();
    drawBranch(length);
  }
}

function drawBranch(length) {
  if (length < random(10, 40)) {
    return;
  }
  myTurtle.moveForward(length * 0.05);
  let branchColor = color(random(0, 100), random(150, 255), random(0, 155));
  stroke(branchColor);

  // Draw this branch
  strokeWeight(length / random(100, 120));
  myTurtle.moveForward(length * 0.5);
  myTurtle.turnLeft(random(10, 25));
  myTurtle.moveForward(length * 0.5);

  let circleColor = color(random(150, 255), random(150, 255), 0);

  fill(circleColor);
  noStroke();
  let circleRadius = random(0, 5);
  let numCircles = random(0, 1);
  for (let i = 0; i < 1; i++) {
    let offsetX = random(-length / 2, length / 2);
    let offsetY = random(0, length / 2);
    ellipse(myTurtle.x + offsetX, myTurtle.y - offsetY, circleRadius * 2);
    circleRadius += random(5, 10);
  }

  // Left child
  myTurtle.pushState();
  myTurtle.turnLeft(random(10, 15));
  drawBranch(length * 0.75);
  myTurtle.popState();

  // Right child
  myTurtle.pushState();
  myTurtle.turnRight(random(10, 15));
  drawBranch(length * 0.75);
  myTurtle.popState();
}

