    let currentColor = lerpColor(startColor, endColor, colorAmount);
    stroke(currentColor);
    drawPetals();
    myTurtle.turnRight(7);
  }

  noLoop();
}

function drawPetals() {
  myTurtle.penDown();
  for (let i = 0; i < 2; i++) {
    drawCircle(60);
    myTurtle.penUp();
    myTurtle.moveForward(10);
    myTurtle.penDown();
  }
  myTurtle.penUp();
  myTurtle.turnLeft(125);
  myTurtle.moveForward(30);
}

function drawCircle(radius) {
  const angle = 360 / 50;
  for (let i = 0; i < 50; i++) {
    myTurtle.moveForward(2 * radius * sin(angle / 1.5));
    myTurtle.turnRight(angle);
  }
}

