function pixelsProcess(gap) {
    bg.loadPixels()
    for (var x = 0; x < width; x += gap) {
        for (var y = 0; y < height; y += gap) {
            let middleIndex = ((y + 2) * width + (x + 2)) * 4
            for (let i = x; i < x + gap; i++) {
                for (let j = y; j < y + gap; j++) {
                    let index = (j * width + i) * 4
                    bg.pixels[index] = bg.pixels[middleIndex]
                    bg.pixels[index + 1] = bg.pixels[middleIndex + 1]
                    bg.pixels[index + 2] = bg.pixels[middleIndex + 2]
                }
            }

        }
    }
    bg.updatePixels()
    image(bg, 0, 0)
}

