  noiseDetail(1, timeSpeed);
  ellipseMode(CENTER);

  // fill(255);
  // noStroke();
  stroke(255, 10);
  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1.2; i += 0.1) {
    for (j = 0; j < 1.2; j += 0.1) {
      let x = lerp(startX, endX, i);
      let y = lerp(startY, endY, i);

      // hint: drive offsetX and offsetY with noise() instead of random()
      let offsetX =
        (0.5 - noise(timeSpeed + i * frequency)) * amplitude * random() * 500;
      let offsetY = (0.5 - noise(timeSpeed + j * frequency)) * amplitude * 1500;
      fill((mouseX * i) / 2, (mouseY * j) / 2, 100, 10);
      ellipse(
        x + offsetX,
        y + offsetY,
        frequency / 100 + random() * 20,
        frequency / 100 + random() * 20
      );
    }
  }
}


