let myTurtle;

function setup() {
  createCanvas(1000, 1000);
  background('black');
  myTurtle = new Turtle();
}

function draw() {
  noLoop(); // Ensures draw() is only called once
  myTurtle.penUp();
  myTurtle.moveTo(300,900); // Move to the starting position
  myTurtle.penDown();
  drawSpirograph(5, 100); // Draw the spirograph with a gap size of 10 degrees and a radius of 80
}

function drawSpirograph(sizeOfGap, radius) {
  let numLoops = 50 / sizeOfGap;
  for (let i = 0; i < numLoops; i++) {
    // Change color for each loop
    stroke(getColor(i));
    drawCircle(myTurtle, radius); // Draw circle with the given radius
    myTurtle.penUp();
    myTurtle.turnLeft(sizeOfGap); // Corrected to use turnLeft instead of left
    myTurtle.moveForward(radius * 2); // Move to the position for the next circle
    myTurtle.turnRight(sizeOfGap); // Corrected to use turnRight instead of right
    myTurtle.penDown();
  }
}

function drawCircle(t, radius) {
  for(let i = 0; i < 360; i++) {
    t.moveForward(radius * Math.PI); // Approximation for moving along the circle's circumference
    t.turnLeft(1); // Corrected to use turnLeft instead of left
  }
}

function getColor(index) {
  const colors = ['violet', 'white', 'blue', 'green', 'yellow', 'orange', 'red'];
  return colors[index % colors.length];
}

